INSERT INTO `app_code` VALUES (4, 1, '﻿\r\n<style>\r\n#load{\r\n	text-align:center;\r\n	padding-top:10vh;\r\n}\r\n</style>\r\n<style>\r\n\r\n.CH_work{\r\n	transition: all 0.15s ease 0s;\r\n}\r\n.appDiv{\r\n	width:100%;\r\n	height:100%;\r\n	display:flex;\r\n	position:absolute;\r\n	left:0;\r\n	top:0;\r\n	transition:all 0.2s;\r\n	background-color:#ffffff;\r\n	flex-direction:column;\r\n}\r\n.appTitle{\r\n	background-color:#ffffff;/*标题背景颜色*/\r\n	height:56px;\r\n	display:flex;\r\n}\r\n.appTitle_Back{\r\n	display:flex;\r\n	width: 56px;\r\n	flex-direction:column;\r\n	justify-content:center;\r\n}\r\n.appTitle_Back>svg{\r\n	margin: 0 auto;\r\n	padding:5px;\r\n}\r\n.appTitle_Back_Active>svg{\r\n	opacity: 0.8;\r\n  background-color: #2f2f2f;\r\n}\r\n.appTitle_Text{\r\n	line-height:56px;\r\n	color:#ffffff;/*标题文字颜色：白色*/\r\n}\r\n.appTitle_More{\r\n	width:40px;\r\n	display:flex;\r\n	flex-direction:column;\r\n	justify-content:center;\r\n	margin-left: auto;\r\n  margin-right: 16px;\r\n}\r\n.appTitle_More_button{\r\n	height: 24px;\r\n	background-color:rgba(255,255,255,0.2);\r\n	border-radius:12px;\r\n}\r\n.appFrame{\r\n	width:100%;\r\n	height:100%;\r\n	flex:1;\r\n	border:0;\r\n	background-color:#ffffff;\r\n	overflow:hidden;\r\n}\r\n.toast{\r\n	position:fixed;\r\n	color:white;\r\n	z-index:9999;\r\n	height:auto;\r\n	line-height:18px;\r\n	text-align:center;\r\n	bottom:8%;\r\n	padding:10px;\r\n	max-width:90%;\r\n	overflow:hidden;\r\n	background-color:rgba(0,0,0,0.5);\r\n	transform:translate3d(-50%,0,0);\r\n	border-radius:10px;\r\n	min-width:30%;\r\n	min-height:14px;\r\n	opacity:0;\r\n	left:50%;\r\n	pointer-events:none;\r\n}\r\n.loading{\r\n	position:fixed;\r\n	left:0px;\r\n	right:0px;\r\n	width:130px;\r\n	height:130px;\r\n	top:50%;\r\n	transform:translate3d(0,-50%,0);\r\n	margin:0px auto;\r\n	text-align:center;\r\n	z-index:99999;\r\n	background-color:rgba(0, 0, 0, 0.6);\r\n	border-radius:5px;\r\n	color:#ffffff;\r\n	padding:4px;\r\n	opacity:0;\r\n	pointer-events:none;\r\n}\r\n@keyframes rotate{\r\n	from{\r\n		transform: rotate(0deg)\r\n	}to{\r\n		transform: rotate(360deg)\r\n	}\r\n}\r\n.loading>div>svg{\r\n	width:40px;height:40px;\r\n	animation: rotate 2s linear infinite;\r\n}\r\n.loading>div{\r\n	\r\n	/*background-image:url(\'/static/in/img/loading.gif\');*/\r\n	background-repeat: no-repeat;\r\n	position: relative;\r\n	top:20%;\r\n	width:30%;\r\n	height:30%;\r\n	margin:0px auto;\r\n	background-size:100% 100%;\r\n}\r\n.loading>span{\r\n	position:absolute;\r\n	bottom:20%;\r\n	left:0px;\r\n	right:0px;\r\n	font-size:14px;\r\n	line-height:14px;\r\n}\r\n/*弹窗开始*/\r\n.alert-hover{\r\n	width:100%;\r\n	height:100%;\r\n	background-color:rgba(0,0,0,0.5);\r\n	opacity:0;\r\n	transition:all 0.2s;\r\n	position:absolute;\r\n	left:0;\r\n	top:0;\r\n	z-index:999999;\r\n}\r\n.alert-hover>.alert{\r\n	width:80%;\r\n	position:relative;\r\n	top:50%;\r\n	margin:0 auto;\r\n	background-color:#ffffff;\r\n	border-radius:10px;\r\n	overflow:hidden;\r\n	transition:all 0.5s;\r\n	transform:translate3d(0,-40%,0) scale(0.01);\r\n}\r\n.alert>.alert-title{\r\n	padding:10px;\r\n}\r\n.alert>.alert-content{\r\n	padding:20px;\r\n	margin-bottom:20px;\r\n}\r\n.alert>.alert-button{\r\n	display:flex;\r\n	overflow:hidden;\r\n}\r\n.alert>.alert-button>.alert-button-item{\r\n	height:36px;\r\n	line-height:36px;\r\n	text-align:center;\r\n	flex:1;\r\n	border:1px solid #fafafa;\r\n	transition:all .2s\r\n}\r\n.alert>.alert-button>.alert-button-item:active{\r\n	background-color:#eee;\r\n}\r\n.alert .alert-radio{\r\n	width:100%;\r\n	height: 2rem;\r\n	line-height: 2rem;\r\n	background-color: #fafafa;\r\n	border-radius: 0.25rem;\r\n	border: 1px solid #efefef;\r\n	text-align: center;\r\n}\r\n.alert .alert-checkbox{\r\n	width:100%;\r\n	height: 2rem;\r\n	line-height: 2rem;\r\n	background-color: #fafafa;\r\n	border-radius: 0.25rem;\r\n	border: 1px solid #efefef;\r\n	text-align: center;\r\n}\r\n.alert .alert-input{\r\n	width:100%;\r\n	border: 1px solid #ced4da;\r\n	padding: 0.375rem 0.75rem;\r\n	border-radius: 0.25rem;\r\n	box-sizing: border-box;\r\n	line-height: 1.5;\r\n}\r\n/*弹窗结束*/\r\n\r\n.notificationSystem{\r\n	position:fixed;\r\n	bottom:50px;\r\n	right:0;\r\n	transition:all 0.5s;\r\n	padding:12px;\r\n	height:140px;\r\n	width:240px;\r\n	overflow:hidden;\r\n	background-color:rgba(2,35,64,0.9);\r\n	color:white;\r\n	border:1px solid rgba(255,255,255,0.53);\r\n	transform:translate3d(110%,0,0);\r\n	transition-timing-function:cubic-bezier(0,0.29,0.38,1.23);\r\n	z-index:9999999;\r\n	pointer-events:none;\r\n}\r\nview{ \r\n	display:block;\r\n}\r\n#fullscreen{\r\n	position:absolute;\r\n	left:0;top:0;\r\n	width:100%;\r\n	height:100%;\r\n	display:none;\r\n}\r\n#appMenu{\r\n	transition:all 0.2s;\r\n	width:100%;\r\n	height:100%;\r\n	background-color:rgba(0,0,0,0.2);\r\n	opacity:0;\r\n	display:flex;\r\n	flex-direction:column;\r\n	justify-content:center;\r\n}\r\n#appMenuList{\r\n	background-color:rgb(255,255,255);\r\n	width:300px;\r\n	margin:0 auto;\r\n	border-radius:10px;\r\n}\r\n#appMenuList>div{\r\n	height:40px;line-height:40px;\r\n	text-align:center;\r\n	border-top:1px solid #eeeeee;\r\n}\r\n#appMenuList>div:active{\r\n	background-color:rgb(122,122,122,122);\r\n}\r\n\r\n</style>\r\n<input id=\"theme-color\" type=\"hidden\" value=\"0xAA8800\">\r\n<div id=\"load\"></div>\r\n<script id=\"appstyle\" type=\"text/html\">\r\nnone{\r\n	display:none;\r\n}\r\n*:not(input):not(textarea){\r\n	-webkit-touch-callout:none;\r\n	-webkit-user-select:none;\r\n	-khtml-user-select:none;\r\n	-moz-user-select:none;\r\n	-ms-user-select:none;\r\n	user-select:none;\r\n	-webkit-tap-highlight-color: transparent;\r\n	touch-action: pan-y;\r\n}\r\n*{\r\n	font-family: Arial;\r\n	zoom:1;\r\n	backface-visibility: hidden;\r\n}\r\ntemplate{\r\n	display:none;\r\n}\r\ninput[type=button],input[type=text],input[type=password]{-webkit-appearance:none;outline:none;}\r\ninput,textarea,.copy{\r\n	-webkit-touch-callout:text;\r\n	-webkit-user-select:text;\r\n	-khtml-user-select:text;\r\n	-moz-user-select:text;\r\n	-ms-user-s