INSERT INTO `app_code` VALUES (26, 8, '\r\n<style>\r\n#load{\r\n	text-align:center;\r\n	padding-top:10vh;\r\n}\r\n</style>\r\n<style>\r\n\r\n.CH_work{\r\n	transition: all 0.15s ease 0s;\r\n}\r\n.appDiv{\r\n	width:100%;\r\n	height:100%;\r\n	display:flex;\r\n	position:absolute;\r\n	left:0;\r\n	top:0;\r\n	transition:all 0.2s;\r\n	background-color:#ffffff;\r\n	flex-direction:column;\r\n}\r\n.appTitle{\r\n	background-color:#ffffff;/*标题背景颜色*/\r\n	height:56px;\r\n	display:flex;\r\n}\r\n.appTitle_Back{\r\n	display:flex;\r\n	width: 56px;\r\n	flex-direction:column;\r\n	justify-content:center;\r\n}\r\n.appTitle_Back>svg{\r\n	margin: 0 auto;\r\n	padding:5px;\r\n}\r\n.appTitle_Back_Active>svg{\r\n	opacity: 0.8;\r\n  background-color: #2f2f2f;\r\n}\r\n.appTitle_Text{\r\n	line-height:56px;\r\n	color:#ffffff;/*标题文字颜色：白色*/\r\n}\r\n.appTitle_More{\r\n	width:40px;\r\n	display:flex;\r\n	flex-direction:column;\r\n	justify-content:center;\r\n	margin-left: auto;\r\n  margin-right: 16px;\r\n}\r\n.appTitle_More_button{\r\n	height: 24px;\r\n	background-color:rgba(255,255,255,0.2);\r\n	border-radius:12px;\r\n}\r\n.appFrame{\r\n	width:100%;\r\n	height:100%;\r\n	flex:1;\r\n	border:0;\r\n	background-color:#ffffff;\r\n	overflow:hidden;\r\n}\r\n.toast{\r\n	position:fixed;\r\n	color:white;\r\n	z-index:9999;\r\n	height:auto;\r\n	line-height:18px;\r\n	text-align:center;\r\n	bottom:8%;\r\n	padding:10px;\r\n	max-width:90%;\r\n	overflow:hidden;\r\n	background-color:rgba(0,0,0,0.5);\r\n	transform:translate3d(-50%,0,0);\r\n	border-radius:10px;\r\n	min-width:30%;\r\n	min-height:14px;\r\n	opacity:0;\r\n	left:50%;\r\n	pointer-events:none;\r\n}\r\n.loading{\r\n	position:fixed;\r\n	left:0px;\r\n	right:0px;\r\n	width:130px;\r\n	height:130px;\r\n	top:50%;\r\n	transform:translate3d(0,-50%,0);\r\n	margin:0px auto;\r\n	text-align:center;\r\n	z-index:99999;\r\n	background-color:rgba(0, 0, 0, 0.6);\r\n	border-radius:5px;\r\n	color:#ffffff;\r\n	padding:4px;\r\n	opacity:0;\r\n	pointer-events:none;\r\n}\r\n@keyframes rotate{\r\n	from{\r\n		transform: rotate(0deg)\r\n	}to{\r\n		transform: rotate(360deg)\r\n	}\r\n}\r\n.loading>div>svg{\r\n	width:40px;height:40px;\r\n	animation: rotate 2s linear infinite;\r\n}\r\n.loading>div{\r\n	\r\n	/*background-image:url(\'/static/in/img/loading.gif\');*/\r\n	background-repeat: no-repeat;\r\n	position: relative;\r\n	top:20%;\r\n	width:30%;\r\n	height:30%;\r\n	margin:0px auto;\r\n	background-size:100% 100%;\r\n}\r\n.loading>span{\r\n	position:absolute;\r\n	bottom:20%;\r\n	left:0px;\r\n	right:0px;\r\n	font-size:14px;\r\n	line-height:14px;\r\n}\r\n/*弹窗开始*/\r\n.alert-hover{\r\n	width:100%;\r\n	height:100%;\r\n	background-color:rgba(0,0,0,0.5);\r\n	opacity:0;\r\n	transition:all 0.2s;\r\n	position:absolute;\r\n	left:0;\r\n	top:0;\r\n	z-index:999999;\r\n}\r\n.alert-hover>.alert{\r\n	width:80%;\r\n	position:relative;\r\n	top:50%;\r\n	margin:0 auto;\r\n	background-color:#ffffff;\r\n	border-radius:10px;\r\n	overflow:hidden;\r\n	transition:all 0.5s;\r\n	transform:translate3d(0,-40%,0) scale(0.01);\r\n}\r\n.alert>.alert-title{\r\n	padding:10px;\r\n}\r\n.alert>.alert-content{\r\n	padding:20px;\r\n	margin-bottom:20px;\r\n}\r\n.alert>.alert-button{\r\n	display:flex;\r\n	overflow:hidden;\r\n}\r\n.alert>.alert-button>.alert-button-item{\r\n	height:36px;\r\n	line-height:36px;\r\n	text-align:center;\r\n	flex:1;\r\n	border:1px solid #fafafa;\r\n	transition:all .2s\r\n}\r\n.alert>.alert-button>.alert-button-item:active{\r\n	background-color:#eee;\r\n}\r\n.alert .alert-radio{\r\n	width:100%;\r\n	height: 2rem;\r\n	line-height: 2rem;\r\n	background-color: #fafafa;\r\n	border-radius: 0.25rem;\r\n	border: 1px solid #efefef;\r\n	text-align: center;\r\n}\r\n.alert .alert-checkbox{\r\n	width:100%;\r\n	height: 2rem;\r\n	line-height: 2rem;\r\n	background-color: #fafafa;\r\n	border-radius: 0.25rem;\r\n	border: 1px solid #efefef;\r\n	text-align: center;\r\n}\r\n.alert .alert-input{\r\n	width:100%;\r\n	border: 1px solid #ced4da;\r\n	padding: 0.375rem 0.75rem;\r\n	border-radius: 0.25rem;\r\n	box-sizing: border-box;\r\n	line-height: 1.5;\r\n}\r\n/*弹窗结束*/\r\n\r\n.notificationSystem{\r\n	position:fixed;\r\n	bottom:50px;\r\n	right:0;\r\n	transition:all 0.5s;\r\n	padding:12px;\r\n	height:140px;\r\n	width:240px;\r\n	overflow:hidden;\r\n	background-color:rgba(2,35,64,0.9);\r\n	color:white;\r\n	border:1px solid rgba(255,255,255,0.53);\r\n	transform:translate3d(110%,0,0);\r\n	transition-timing-function:cubic-bezier(0,0.29,0.38,1.23);\r\n	z-index:9999999;\r\n	pointer-events:none;\r\n}\r\nview{ \r\n	display:block;\r\n}\r\n#fullscreen{\r\n	position:absolute;\r\n	left:0;top:0;\r\n	width:100%;\r\n	height:100%;\r\n	display:none;\r\n}\r\n#appMenu{\r\n	transition:all 0.2s;\r\n	width:100%;\r\n	height:100%;\r\n	background-color:rgba(0,0,0,0.2);\r\n	opacity:0;\r\n	display:flex;\r\n	flex-direction:column;\r\n	justify-content:center;\r\n}\r\n#appMenuList{\r\n	background-color:rgb(255,255,255);\r\n	width:300px;\r\n	margin:0 auto;\r\n	border-radius:10px;\r\n}\r\n#appMenuList>div{\r\n	height:40px;line-height:40px;\r\n	text-align:center;\r\n	border-top:1px solid #eeeeee;\r\n}\r\n#appMenuList>div:active{\r\n	background-color:rgb(122,122,122,122);\r\n}\r\n\r\n</style>\r\n<input id=\"theme-color\" type=\"hidden\" value=\"0xAA8800\">\r\n<div id=\"load\"></div>\r\n<script id=\"appstyle\" type=\"text/html\">\r\nnone{\r\n	display:none;\r\n}\r\n*:not(input):not(textarea){\r\n	-webkit-touch-callout:none;\r\n	-webkit-user-select:none;\r\n	-khtml-user-select:none;\r\n	-moz-user-select:none;\r\n	-ms-user-select:none;\r\n	user-select:none;\r\n	-webkit-tap-highlight-color: transparent;\r\n	touch-action: pan-y;\r\n}\r\n*{\r\n	font-family: Arial;\r\n	zoom:1;\r\n	backface-visibility: hidden;\r\n}\r\ntemplate{\r\n	display:none;\r\n}\r\ninput[type=button],input[type=text],input[type=password]{-webkit-appearance:none;outline:none;}\r\ninput,textarea,.copy{\r\n	-webkit-touch-callout:text;\r\n	-webkit-user-select:text;\r\n	-khtml-user-select:text;\r\n	-moz-user-select:text;\r\n	-ms-user-select:text;\r\n	user-select:text;\r\n}\r\nscroll{\r\n	overflow:scroll;\r\n	width:100%;\r\n	height:100%;\r\n	display: block;\r\n}\r\nview{\r\n	display:block;\r\n}\r\npage{\r\n	height:100vh;\r\n	width:100vw;\r\n	display:none;\r\n}\r\niscroll,iscroll-y,iscroll-x{\r\n	backface-visibility: hidden;  \r\n	perspective:1000;\r\n	width:100%;\r\n	height:100%;\r\n	display: block;\r\n}\r\nli{\r\n	list-style:none;\r\n}\r\n.ch-ripple{\r\n	position:relative;\r\n	overflow:hidden;\r\n}\r\nimg[src=\"\"],img:not([src]){\r\n	opacity:0;\r\n}\r\n/*@-o-keyframes ch_bar{from{background-position:40px 0}to{background-position:0 0}}\r\n@keyframes ch_bar{from{background-position:40px 0}to{background-position:0 0}}*/\r\n</script>\r\n<script id=\"jsapi\" type=\"text/html\">\r\nvar app=null;//全局拿到app对象\r\nvar api=null;//应用内全局变量，接口对象，所有接口通过此接口来调用\r\nvar getApp;//给轻应用通过函数获取app对象\r\nvar getApi;//给轻应用通过函数获取api对象\r\n\r\ndocument.body.addEventListener(\'touchmove\',function(e){e.preventDefault();},{passive:false});//禁止下拉\r\nif(!NodeList.prototype.forEach)NodeList.prototype.forEach=Array.prototype.forEach;//兼容ES5\r\ndocument.oncontextmenu=function(e){e.preventDefault();return false;};//禁止长按菜单\r\n/*安全禁用本地缓存，作者CH*/\r\n[\"indexedDB\",\"openDatabase\",\"localStorage\"].forEach(function(d){//禁用浏览器缓存\r\n	try{\r\n		Object.defineProperty(window,d,{\r\n			get:function(){return void 0},\r\n		});\r\n	}catch(e){};\r\n});\r\nObject.defineProperty(document,\"cookie\",{//禁用cookie\r\n    get:function(){\r\n        return void 0;\r\n    },\r\n});\r\n/*安全禁用本地缓存，作者CH*/\r\n+function(){\r\n	var APPobj;//app副本，防止被赋null\r\n	var environment;//应用环境，框架传过来的env\r\n	var o=top.o;//获取内核句柄\r\n	var handle={};//应用私有句柄(应用不允许读取和修改的)供框架使用操纵的\r\n	\r\n	handle.api=function(){return api;};\r\n	handle.eval=function(code,data,that){return eval(\'(\'+code+\')\')};\r\n	handle.stringify=function(data){return JSON.stringify(data);};\r\n	handle.callFunc=function(func,string){\r\n		if(typeof app[func]!==\"function\")return;\r\n		var res=app[func](JSON.parse(string));\r\n		if(typeof res ===\"undefined\")return null;\r\n		return res;\r\n	};\r\n	//跨域修复Fix CH 2019-12-27 Fix2 2020-01-02\r\n	(function(){var a=setTimeout,b=clearTimeout,c=setInterval,d=clearInterval,l=[],n=0;setTimeout=function(e,f){n++;+function(n){requestAnimationFrame(function(){if(l[n]===!1)return;l[n]=a(e,f)})}(n);return n};clearTimeout=function(g){b(l[g]);l[g]=!1};setInterval=function(e,f){n++;+function(n){requestAnimationFrame(function(){if(l[n]===!1)return;l[n]=c(e,f)})}(n);return n};clearInterval=function(g){d(l[g]);l[g]=!1}})();\r\n	/*(function() {\r\n		\r\n		\r\n			var a = setTimeout;\r\n			var b = clearTimeout;\r\n			var c = setInterval;\r\n			var d = clearInterval;\r\n			var l = [];\r\n			var n = 0;\r\n			setTimeout = function(e, f) {\r\n					n++;\r\n					(function(n){\r\n						requestAnimationFrame(function() {\r\n								if (l[n] === !1) return;\r\n								l[n] = a(e, f);\r\n						});\r\n					})(n);\r\n					return n;\r\n			};\r\n			clearTimeout = function(g) {\r\n					b(l[g]);\r\n					l[g] = !1;\r\n			};\r\n			setInterval = function(e, f) {\r\n					n++;\r\n					(function(n){\r\n						requestAnimationFrame(function() {\r\n								if (l[n] === !1) return;\r\n								l[n] = c(e, f);\r\n						});\r\n					})(n);\r\n					return n;\r\n			};\r\n			clearInterval = function(g) {\r\n					d(l[g]);\r\n					l[g] = !1;\r\n			};\r\n	})();*/\r\n	/*(function(){var a=setTimeout;var b=clearTimeout;var c=setInterval;var d=clearInterval;var l=[];setTimeout=function(e,f){var n=l.length;requestAnimationFrame(function(){if(l[n]===!1)return;l[n]=a(e,f);});return n+1;};clearTimeout=function(g){requestAnimationFrame(function(){b(l[g-1]);l[g-1]=!1;});};setInterval=function(e,f){var n=l.length;requestAnimationFrame(function(){if(l[n]===!1)return;l[n]=c(e,f);});return n+1;};clearInterval=function(g){requestAnimationFrame(function(){d(l[g-1]);l[g-1]=!1;});};})();*/\r\n\r\n	//跨域修复Fix CH 2019-12-27\r\n	app=function(html,js,env){\r\n		var performance=top.window.performance;\r\n		document.domain = o.config.subDomain;\r\n		environment=env;\r\n		handle.env=env;//传入的是应用pcb\r\n		\r\n		//env加载成功\r\n		document.body.innerHTML=html;//消耗<10ms正常\r\n		var s=document.createElement(\"script\");\r\n		//HTML加载成功\r\n		\r\n		//执行前没有api，安全\r\n		app=function(c){app=APPobj=c;};//传入代码,防止重复调用\r\n		//加载接口所需的\r\n		s.type=\"text/javascript\";\r\n		try{s.appendChild(document.createTextNode(js))}catch(ex){s.text=js};\r\n		try{\r\n			document.body.appendChild(s);//同步 消耗<10ms正常\r\n		}catch(e){\r\n			console.warn(\"小程序onLoad执行出错！\");\r\n		}\r\n		\r\n		if(typeof app.data !==\"object\")app.data={};//防止没有data\r\n		//这种方法和eval有区别，这里不应该用eval\r\n		//到这里有app实体了，但是还没有运行\r\n		//JS加载成功\r\n		\r\n		handle.app=APPobj;//装载app\r\n		api=o.getApi(document,handle);//内存在同一个通道，可以获得\r\n		api.asynUpdate();//异步更新\r\n		//开始加载api\r\n		\r\n		\r\n		requestAnimationFrame(function(){\r\n			api.bind();//绑定对象 消耗170ms，异常，需要优化\r\n		});\r\n		api.loadJS=function(code){\r\n			var s=document.createElement(\"script\");\r\n			s.type=\"text/javascript\";\r\n			try{s.appendChild(document.createTextNode(code))}catch(ex){s.text=code};\r\n			document.body.appendChild(s);\r\n		};\r\n		api.loadCSS=function(code){\r\n			var s=document.createElement(\"style\");\r\n			s.type=\"text/css\";\r\n			try{s.appendChild(document.createTextNode(code))}catch(ex){s.text=code};\r\n			document.body.appendChild(s);\r\n		};\r\n		api.setData=function(json){//设置本应用数据\r\n			for(var j in json){app.data[j]=json[j];}\r\n		};\r\n		\r\n		//api加载完成，开始运行\r\n		requestAnimationFrame(function(){\r\n			if(app.onLoad instanceof Function){\r\n				app.onLoad({referer:env.option.referer,data:env.option.data});//消耗<10ms正常\r\n			}else{\r\n				//找不到入口函数\r\n				\r\n			}\r\n		});\r\n		return handle;\r\n	};\r\n	\r\n	\r\n	\r\n	/*window.addEventListener(\'message\',function(e){\r\n		if(e.data.type==\"html\"){\r\n			document.body.innerHTML=e.data.code;\r\n			//到了这里的时候前端已经装载好了\r\n		}else if(e.data.type==\"script\"){\r\n			var s=document.createElement(\"sc