INSERT INTO `app_code` VALUES (29, 9, '\r\n<style>\r\n#load{\r\n	text-align:center;\r\n	padding-top:10vh;\r\n}\r\n</style>\r\n<style>\r\n\r\n.CH_work{\r\n	transition: all 0.15s ease 0s;\r\n}\r\n.appDiv{\r\n	width:100%;\r\n	height:100%;\r\n	display:flex;\r\n	position:absolute;\r\n	left:0;\r\n	top:0;\r\n	transition:all 0.2s;\r\n	background-color:#ffffff;\r\n	flex-direction:column;\r\n}\r\n.appTitle{\r\n	background-color:#ffffff;/*标题背景颜色*/\r\n	height:56px;\r\n	display:flex;\r\n}\r\n.appTitle_Back{\r\n	display:flex;\r\n	width: 56px;\r\n	flex-direction:column;\r\n	justify-content:center;\r\n}\r\n.appTitle_Back>svg{\r\n	margin: 0 auto;\r\n	padding:5px;\r\n}\r\n.appTitle_Back_Active>svg{\r\n	opacity: 0.8;\r\n  background-color: #2f2f2f;\r\n}\r\n.appTitle_Text{\r\n	line-height:56px;\r\n	color:#ffffff;/*标题文字颜色：白色*/\r\n}\r\n.appTitle_More{\r\n	width:40px;\r\n	display:flex;\r\n	flex-direction:column;\r\n	justify-content:center;\r\n	margin-left: auto;\r\n  margin-right: 16px;\r\n}\r\n.appTitle_More_button{\r\n	height: 24px;\r\n	background-color:rgba(255,255,255,0.2);\r\n	border-radius:12px;\r\n}\r\n.appFrame{\r\n	width:100%;\r\n	height:100%;\r\n	flex:1;\r\n	border:0;\r\n	background-color:#ffffff;\r\n	overflow:hidden;\r\n}\r\n.toast{\r\n	position:fixed;\r\n	color:white;\r\n	z-index:9999;\r\n	height:auto;\r\n	line-height:18px;\r\n	text-align:center;\r\n	bottom:8%;\r\n	padding:10px;\r\n	max-width:90%;\r\n	overflow:hidden;\r\n	background-color:rgba(0,0,0,0.5);\r\n	transform:translate3d(-50%,0,0);\r\n	border-radius:10px;\r\n	min-width:30%;\r\n	min-height:14px;\r\n	opacity:0;\r\n	left:50%;\r\n	pointer-events:none;\r\n}\r\n.loading{\r\n	position:fixed;\r\n	left:0px;\r\n	right:0px;\r\n	width:130px;\r\n	height:130px;\r\n	top:50%;\r\n	transform:translate3d(0,-50%,0);\r\n	margin:0px auto;\r\n	text-align:center;\r\n	z-index:99999;\r\n	background-color:rgba(0, 0, 0, 0.6);\r\n	border-radius:5px;\r\n	color:#ffffff;\r\n	padding:4px;\r\n	opacity:0;\r\n	pointer-events:none;\r\n}\r\n@keyframes ro