INSERT INTO `app_code` VALUES (44, 1, 'app({\n	setting:{\n		title:\"这是例子应用\",\n		color:\"#AA6600\",\n	},\n	data:{//应用数据\n		text:\"Hello World\",\n		\n	},\n	onLoad:function(event){//启动应用\n		//这里是应用启动时触发\n		api.toast(\"触发了onLoad事件！\");\n		api.import([\"/glutybui/glutybui.css\",\"/static/cdn/vconsole.min.js\"],function(){\n			var vConsole = new VConsole();\n		});\n	},\n	onHide:function(){//应用隐藏时\n		api.toast(\"触发了onHide事件！\");\n	},\n	onShow:function(){//应用显示时\n		api.toast(\"触发了onShow事件！\");\n	},\n	onBack:function(){//用户触摸返回键时\n		//return false;//拦截返回键，应用内自行处理\n		api.toast(\"触发了onBack事件！\");\n	},\n	onUnload:function(){//应用销毁时\n		api.toast(\"触发了onUnload事件！\");\n	},\n	onEvent:function(event){//当接收到事件时触发\n		console.log(event);\n		\n		if(event.type==\"scan\"){\n			api.toast(\"扫描结果：\"+event.data);\n		}else if(event.type==\"location\"){\n			api.toast(\"位置信息：\"+JSON.stringify(event.data));\n		}else if(event.type==\"uuid\"){\n			api.toast(\"设备唯一码：\"+JSON.stringify(event.data));\n		}else if(event.type==\"device\"){\n			api.toast(\"设备信息：\"+JSON.stringify(event.data));\n		}else if(event.type==\"onSignature\"){\n			api.toast(\"数字签名\"+JSON.stringify(event.data));\n		}else{\n			api.toast(\"onEvent:\"+JSON.stringify(event));\n		}\n	},\n	onMessage:function(event1,event2){//当应用接收到其他应用发来的消息时\n		api.toast(\"触发了onMessage事件！\");\n		console.log(event1,event2);\n	},\n	onUpdate:function(event){//应用更新时\n		//拦截内置更新机制，自行处理，谨慎使用，小心应用无法正常更新。\n		api.toast(\"触发了onUpdate事件！\");\n		//return false;\n	},\n	//其它函数为自定义函数\n	buttonClick:function(event){\n		console.log(event);//点击事件的传参\n		switch(event.target.dataset.buttontype|0){\n			case 1:\n				api.reload();\n				return;\n			case 2://测试toast\n				api.toast(\"toast 测试5秒后消失\",5000);\n				return;\n			case 3://显示等待\n				api.showLoading(\"正在等待...\");\n				return;\n			case 4://隐藏等待\n				api.hideLoading();\n				return;\n			case 5://获取公钥\n				api.toast(api.get_App());\n				return;\n			case 6://获取进程句柄\n				api.toast(api.get_Pid());\n				return;\n			case 7://打开自己\n				api.app.openapp(api.get_App(),{newer:true});\n				return;\n			case 8://关闭自己\n				api.close();\n				return;\n			case 9:\n				api.update(function(){\n					api.reload();//更新完成后重启自己\n				});\n				//或者updateReload()\n				return;\n			\n			case 10:\n				api.iniSet(function(){\n					api.toast(\"已设置test为Hello World\");\n				},\"test\",\"Hello World\");\n				return;\n			case 11:\n				api.toast(api.get_deviceId());\n				return;\n			case 12:\n				api.iniGet(function(data){\n					api.toast(\"test的值为\"+data);\n				},\"test\",\"默认值\");\n				return;\n			case 13:\n				api.storage.set(\"ch1\",\"hello1\",function(){\n					api.toast(\"ch1设置完成！\");\n				});\n				api.storage.set(\"ch2\",\"hello2\",function(){\n					api.toast(\"ch2设置完成！\");\n				});\n				return;\n			case 14:\n				api.storage.get(\"ch1\",function(data){\n					api.toast(\"ch1的值为：\"+data);\n				});\n				return;\n			case 15:\n				api.storage.keys(\"ch\",function(value,key){\n					api.toast(key+\"的值为：\"+value);\n				});//获取以ch开头的键\n				return;\n			case 16:\n				api.getApiVersion(function(data){\n					api.toast(\"当前API库版本为：\"+data);\n				});\n				return;\n			case 17:\n				api.getVersion(function(data){\n					api.toast(\"当前应用版本为：\"+data);\n				});\n				return;\n			case 18:\n				//api.trigger(\"Event1\",\"Some Text\");//各应用有序接收，可以被拦截或修改\n				api.triggerAll(\"Event1\",\"Some Text\");//无序接收\n				return;\n			case 19:\n				api.sendMessage(api.get_Pid(),\"Some Value\");//给某应用传递值\n				return;\n			case 20:\n				api.vibrate(2000);\n				return;\n			case 21:\n				api.landscape();\n				return;\n			case 22:\n				api.portrait();\n				return;\n			case 23:\n				api.reboot();\n				return;\n			case 24:\n				api.copy(\"复制了一个文本！\");return;\n			case 25:\n				api.toast(api.is_Android());return;\n			case 26:\n				api.toast(api.is_IOS());return;\n			case 27:\n				api.toast(api.is_PC());return;\n			case 28:\n				api.toast(api.is_Yiban());return;\n			case 29://获取平台类型\n				/*\n					0 浏览器\n					1 安卓易班\n					2 IOS易班\n					3 微信内置浏览器\n					4 微信小程序\n					5 QQ\n					6 桂林理工大学\n				*/\n				api.toast(api.get_Platform());return;\n			case 30://获取设备类型\n				/*\n					1 PC\n					2 安卓\n					3 IOS\n				*/\n				api.toast(api.get_Device());return;\n			case 31://获取设备ua\n				api.toast(navigator.userAgent);return;\n			case 32:\n				document.getElementById(\"runResult\").value=eval(document.getElementById(\"runCode\").value);\n				return;\n			case 33:\n				document.getElementById(\"runCode\").value=\"\";\n				return;\n			case 34:\n				api.copy(document.getElementById(\"runCode\").value);\n				api.toast(\"复制成功！\");\n				return;\n			case 35:\n				api.toast(\"粘贴失败！\");\n				return;\n			case 36:\n				api.alert(\"您确定要进行此操作吗\",\"我是标题\",[\"确认\",\"取消\"],function(event){\n					console.log(event);\n					if(event.id==0){\n						api.alert(\"你点了第一个按钮\",\"\",[\"确定\"]);\n					}else if(event.id==1){\n						api.alert(\"你点了第二个按钮\",\"标题\",[\"是啊\"]);\n					}\n				});\n				/*\n				下面这种方式更高级！\n				api.alert(\n				{\n					title:\"我是标题\",\n					text:\"您确定要进行此操作吗\",\n					button:[\"确认\",\"取消\"],\n					content:[],\n					click:function(event){\n						console.log(event);\n						if(event.id==0){\n							api.alert(\"你点了第一个按钮\",\"\",[\"确定\"]);\n						}else if(event.id==1){\n							api.alert(\"你点了第二个按钮\",\"标题\",[\"是啊\"]);\n						}\n					},\n				);\n				*/\n				return;\n			case 37:\n				api.__systemUpdate();\n				api.reboot();\n				api.toast(\"成功\");\n				return;\n			case 38:\n				api.color(\"#66AA00\");\n				api.toast(\"标题栏颜色修改成功1！\");\n				return;\n			case 39:\n				api.color(\"#6600DD\");\n				api.toast(\"标题栏颜色修改成功2！\");\n				return;\n			case 40:\n				api.title(\"标题1\");\n				api.toast(\"标题栏内容修改成功1！\");\n				return;\n			case 41:\n				api.title(\"标题2\");\n				api.toast(\"标题栏颜色修改成功2！\");\n				return;\n			case 42:\n				api.scan(function(res){\n					console.log(\"scan result\",res);\n				});\n				return;\n			case 43:\n				api.phone(\"13888888888\",function(res){\n					console.log(res);\n				});\n				return;\n			case 44:\n				api.location(function(res){\n					console.log(\"result\",res);\n				});\n				return;\n			case 45:\n				api.uuid(function(res){\n					console.log(\"result\",res);\n				});\n				return;\n			case 46:\n				api.deviceInfo(function(res){\n					console.log(\"result\",res);\n				});\n				return;\n			case 47:\n				api.AppSignature(function(res){\n					console.log(\"result\",res);\n				});\n				return;\n			case 48:\n				api.socket({type:\"message_push\",data:{title:666,content:{\n					tag:\"view\",\n					attribute:{\"style\":\"background-color:#666666;border-radius:50%;padding:10px;text-align:center;\"},\n					text:\"测试成功！这里是自定义内容！\",\n				}}},function(e){\n					console.log(e)\n				})\n				return;\n			case 49:\n				api.toast(\"成功：\"+\n					setTimeout(function(){\n						alert(\"ok\");\n					},1000)\n				);\n				return;\n			case 50:\n				api.__systemClear();\n				api.toast(\"请稍等...\");\n				return;\n			default:\n				api.toast(\"无效的按钮！\");\n		}\n		\n	},\n	\n});', '2020-01-07 23:38:26', NULL, 6, 2);
INSERT INTO `app_code` VALUES (45, 2, '<style type=\"text/css\">\n	.body{\n		width: 100%;\n		height: 100%;\n		overflow:hidden;\n		position: relative;\n	}\n	.wallpaper{\n		position: absolute;\n		width:100%;\n		height: 100%;\n		left:0;\n		top:0;\n		z-index: -1;\n		overflow: hidden;\n		background-size: 100% 100%;\n		background-position: center center;/*background-image: url(\'\');*/\n	}\n	.headimg{\n		position: absolute;\n		top:5%;\n		left:5%;\n		width:40px;\n		height: 40px;\n		border-radius: 50%;\n	}\n	#desktop{\n		width:200vw;\n		height:100%;\n	}\n	.desktop_page{\n		width:100vw;\n		height:100%;\n		float:left;\n		position: relative;\n	}\n	.app {\n			text-align: center;\n			display: flex;\n			justify-content: center;\n			flex-direction: column;\n			position: absolute;\n			padding: 4px;\n			box-sizing: border-box;\n			transition: transform 0.2s,opacity 0.2s;\n	}\n	.app>view {\n			width: 36px;\n			height: 36px;\n			overflow: hidden;\n			border-radius: 4px;\n			margin: 0 auto;\n			margin-bottom: 12px;\n			background-size: 100% 100%;\n	}\n	.app>text {\n			color: #dddddd;\n			font-size: 11.5px;\n			overflow: hidden;\n			text-overflow: ellipsis;\n			white-space: nowrap;\n			text-shadow: 1px 1px 2px #000;\n	}\n	.app:hover {\n			border: 1px solid rgba(255,255,255,0.25);\n			background-color: rgba(239,255,236,0.25);\n	}\n	.widget {\n			overflow: hidden;\n			position: absolute;\n			transition: all 0.2s;\n	}\n	#rubish{\n			position: absolute;\n			bottom: 0;\n			width: 100%;\n			transition: all 0.2s;\n			height: 0px;\n			overflow:hidden;\n			text-align:center;\n			z-index: 1;\n	}\n	.rubish_free::after{\n		content: \'\';\n			width: 140%;\n			height: 200px;\n			position: absolute;\n			left: -20%;\n			top: 0;\n			z-index: -1;\n			border-radius: 50% 50% 0 0;\n			background: #1496f1;\n			transition:all 0.3s;\n	}\n	.rubish_active::after{\n		content: \'\';\n			width: 140%;\n			height: 200px;\n			position: absolute;\n			left: -20%;\n			top: 0;\n			z-index: -1;\n			border-radius: 50% 50% 0 0;\n			background: #ff3320;\n			transition:all 0.3s;\n	}\n\n	#rubish svg{\n		position: relative;\n		top:10px;\n		width:20px;\n		height:20px;\n	}\n	#debug{\n		position: absolute;\n		top:0;\n		height:20px;\n		width:100%;\n		background-color: white;\n		text-align: center;\n		display:none;\n	}\n\n\n\n.date{\n	height:118px;\n	margin:0 auto;\n	color:#ffffff;\n	position:relative;\n	/*background-color:#e1e2e3;*/\n	/*margin-top:50px;*/\n}\n.now_date{\n	font-size:26px;\n	margin-left:8px;\n	padding-top:50px;\n}\n.now_weekday{\n	font-size:11px;\n	margin-left:12px;\n	padding-top:1px;\n}\n\n.recent{\n	/*height:155px!important;*/\n	margin-top:13px!important;\n}\n.action{\n	\n}\n.card{\n	min-height:34px;\n	/*background-color:#e1e2e3;*/\n	background-color:rgba(255,255,255,0.8);\n	margin-bottom:5px;\n	/*opacity:0.85;*/\n	border-radius:4px;\n	/*box-shadow: 3px 3px 30px #dddddd;*/\n}\n.card_title{\n	position:relative;\n	width:100%;\n	height:28px;\n	padding-top:5px;\n	background-color:rgba(255,255,255,0.8);\n	border-radius:4px;\n}\n.card_title_text{\n	position:absolute;\n	font-size:13px;\n	font-weight:bold;\n	/* left:12%; */\n	top:25%;\n	left:3%;\n}\n.activity_item{\n	margin-left:3%;\n	padding-bottom:15px;\n	font-size:11px;\n	padding-top:20px;\n	position:relative;\n	border-top:1px #DCDCDC solid;\n	margin-right:3%;\n}\n.activity_item:first-of-type{\n	border-top:0px #DCDCDC solid!important;\n	padding-top:15px!important;\n}\n#activity_list{\n	padding-bottom:10px;\n}\n.activity_name{\n	font-size:15px;\n	margin-bottom:5px;\n	font-weight:bold;\n}\n.activity_team{\n	margin-bottom:5px;\n}\n.activity_time{\n	/*border-bottom:1px #DCDCDC solid;*/\n}\n.activity_now{\n	position:absolute;\n	top:20px;\n	right:0;\n	font-size:10px;\n	padding:4px;\n	border:1px #DCDCDC solid;\n	border-radius:5px;\n	background-color:rgba(255,255,255,0.8);\n	/*box-shadow: 3px 3px 30px #dddddd;*/\n}\n#user_head{\n	height: 70px;width: 70px;border-radius: 50%;margin-right: 10px;\n	position: absolute;right:0;top: 30%;background-size: 100% 100%;box-shadow:0 0 5px 1px rgba(0,0,0,0.5);\n}\n.myhome_space{\n	width: 42px;\n    height: 42px;\n    background-size: 100% 100%;\n    margin: 0px auto;\n}\n.search{\n	width:89.5%;\n	height:78px;\n	margin:0 auto;\n	/*background-color:#e1e2e3;*/\n	padding: 4px;\n	margin-top:22px;\n	background-color:#ffffff;\n	opacity:0.85;\n	border-radius:4px;\n}\n.search_box{\n	width: 100%;display: flex;justify-content: space-around;\n}\n.search_box_img{\n	width: 35px;height: 35px;background-size: 100% 100%;margin: 0px auto;background-color: #fff2fa;\n	border-radius: 50%;\n}\n.search_box_text{\n	text-align: center;padding-top: 4px;\n}\n/*.quick{\n	width:100%;height:100%;border-radius:10px;background-color: #d1d1d1;display:flex;height:98px;\n}\n.quick>div{\n	padding:7px;width:100%;height:100%;box-sizing: border-box;\n}\n.quick>div>div{\n	background-color: #ffffff;height:100%;border-radius:10px;box-shadow: inset 0 0 20px 0px #e1e1e1;\n	text-align:center;\n	display:flex;\n	flex-direction:column;\n	justify-content:space-around;\n	color:#666666;\n	font-size:14px;\n}\n.quick>div>div>img:active{\n	transform:scale(0.95);\n	filter: drop-shadow(0px 6px 4px #a1a1a1);\n}\n.quick>div>div>img{\n	width:65%;\n	margin:0 auto;\n	transition: all 0.2s;\n	filter: drop-shadow(0px 10px 10px #a1a1a1);\n	\n}\n.quick>div>div>text{\n	text-shadow: 0px 2px 8px #666666;\n}*/\n.quick{\n	width:100%;height:100%;border-radius:10px;background-color: rgba(255,255,255,0.8);display:flex;height:88px;\n}\n.quick>div{\n	padding:7px;width:100%;height:100%;box-sizing: border-box;\n	display:flex;\n	flex-direction:column;\n	justify-content:space-around;\n	color:#222222;\n	font-size:14px;\n	text-align:center;\n}\n.quick>div>img:active{\n	transform:scale(0.95);\n	filter: drop-shadow(0px 6px 4px #a1a1a1);\n}\n.quick>div>img{\n	width:65%;\n	margin:0 auto;\n	transition: all 0.2s;\n	/* filter: drop-shadow(0px 5px 5px rgba(0,0,0,0.1)); */\n	\n}\n.quick>div>text{\n	/*text-shadow: 0px 2px 8px #666666;*/\n}\n</style>\n<div