色管理\n			if(!message.data.type)return;\n			if(message.data.type==\"setThemeColor\"){//设置主题颜色\n				app.setThemeColor(message.data.data);\n				api.iniSet(function(v){\n					\n				},\"themeColor\",message.data.data);\n			}\n			if(message.data.type==\"getThemeColor\"){\n				api.iniGet(function(v){\n					api.sendMessage(message.referer.pid,{\n						type:\"themeColor\",\n						data:v\n					});\n				},\"themeColor\",\"\");\n			}\n		}\n		if(message.referer.app==\"dbd0520149a111ef36f85782fffb24582dc870d2\"){//桌面设置应用可以多加的通信\n			if(!message.data.type)return;\n			if(message.data.type==\"reset_desktop\"){\n				app.desktop_reload();\n				return;\n			}\n		}\n		if(message.referer.app==\"67ae28d4c97c61f7a1a3c4a6fbd5afb1def1f626\"){//引导\n			if(!message.data.type)return;\n			if(message.data.type==\"reset_desktop\"){\n				// app.reset_desktop();\n				api.iniSet(function(){\n					app.desktop_reload();\n				},\"first\",new Date().getTime());\n				return;\n			}\n			if(message.data.type==\"saveWallPaper\"){\n				api.iniSet(function(v){\n					api.iniSet(function(v){\n						//保存成功！\n						document.getElementsByClassName(\"wallpaper\")[0].style.backgroundColor=\"\";\n						document.getElementsByClassName(\"wallpaper\")[0].style.backgroundImage=\"url(\"+message.data.data+\")\";\n					},\"wallPaperSource\",message.data.data);\n				},\"wallPaperType\",\"image\");\n			}\n\n		}\n		//下面是其他应用传值\n		var data=message.data;\n		if(!data)return;\n		if(data.type==\"changeWidget\"){\n			var d=document.getElementsByClassName(\"widget\");\n			for(var i=0;i<d.length;i++){\n				if(d[i].getAttribute(\"data-widget\")==message.referer.app+\"_\"+data.id){\n					var preview=app.generateHTML(data.data,message.referer.pid);\n					d[i].innerHTML=\"\";\n					d[i].appendChild(preview);\n				}\n			}\n			app.pushQueue(function(finish){//更新内容\n				api.iniGet(function(d){\n					if(!d.length)finish();\n					for(var i in d){\n						if(d[i].id==message.referer.app+\"_\"+data.id){\n							//这里开始换数据\n							d[i].data=data.data;\n							api.iniSet(function(){\n								finish();\n							},\"widget\",d);\n							return;\n						}\n					}\n				},\"widget\",[]);\n			});\n			api.bind();\n		}else if(data.type==\"widget\"){\n			// console.log(\"请求注册小组件\");\n			// console.log(message);\n			if(!data.id)return;\n			if(!data.size)return;\n			//if(!data.data)return;\n			data.data=data.data||\"\";\n			if(!data.title)return;\n			app.pushQueue(function(finish){\n				api.iniGet(function(d){\n					if(d.length){\n						for(var i in d){\n							if(d[i].id==message.referer.app+\"_\"+data.id){//这里开始换数据\n								d[i].data=data.data;\n								// api.iniSet(function(){\n								finish();\n								// },\"widget\",d);\n								return;\n							}\n						}\n					}\n					// console.log(\"开始注册新小组件！\");\n					d.push({\n						id:message.referer.app+\"_\"+data.id,\n						size:data.size,\n						data:data.data,\n						title:data.title,\n						app:message.referer.app,\n						widget:data.id\n					});\n					api.iniSet(function(){\n						finish();\n					},\"widget\",d);\n					// api.toast(\"已成功注册新的桌面小组件！\");\n				},\"widget\",[]);\n			});\n		}\n	},refresh_wallpaper:function(){\n		api.iniGet(function(v){\n			api.iniGet(function(v2){\n				if(v==\"color\"){\n					api.color(v2);\n					document.getElementsByClassName(\"wallpaper\")[0].style.backgroundColor=v2;\n					document.getElementsByClassName(\"wallpaper\")[0].style.backgroundImage=\"\";\n				}else{\n					api.iniGet(function(v3){\n						if(v3.l&&v3.l==v2.length)api.color(v3.c);\n						else{\n							var img = new Image;\n							img.onload = function(){\n								var canvas = document.createElement(\"canvas\");\n								canvas.width = img.width;  \n							    canvas.height = img.height; \n								var ctxt = canvas.getContext(\'2d\');\n								ctxt.drawImage(img, 0, 0);\n								var data = ctxt.getImageData(1, 1, 1, 1).data;//读取整张图片的像素。\n								var rgb = data.toString().split(\',\');\n							    var r = parseInt(rgb[0]);\n							    var g = parseInt(rgb[1]);\n							    var b = parseInt(rgb[2]);\n							    var hex = \"#\" + ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1);\n							    api.color(hex);\n							    api.iniSet(function(){},\"top_piexl\",{l:v2.length,c:hex});\n							}\n							img.src = v2;\n						}\n					},\"top_piexl\",{});\n					document.getElementsByClassName(\"wallpaper\")[0].style.backgroundImage=\"url(\"+v2+\")\";\n					document.getElementsByClassName(\"wallpaper\")[0].style.backgroundColor=\"\";\n				}\n			},\"wallPaperSource\",\"#121212\");//默认白色背景\n		},\"wallPaperType\",\"color\");\n	},open_mine:function(){\n		api.app.openapp(\'09ce468b2bf8c8f7a9e64b8bc94a92d7407cafd9\');\n	},scan:function(){\n		api.scan(function(res){\n			var value=res.value.value\n			if(value.substring(0,9)==\"inglut://\"){\n				api.toast(\"已扫描到结果！\");\n				api.app.openapp(\"750afd0b063557ff76c2eecdf62a18c62dbffb1f\",{data:value});\n			}else\n				api.toast(\"无效的易班二维码！\");\n			\n		});\n	},onUpdate: function(event) { //应用更新时\n		//应用自动更新机制，处理不正确将导致应用无法正常更新。\n		// console.log(\"触发了onUpdate事件！\");\n		//return false;\n	},desktop_reload:function(){\n		api.iniSet(function(){\n			api.toast(\"桌面重置完成！\");\n			api.reload({data:{type:\"reset_desktop\"}});\n		},\"desktop\",{\"widget\":[{\"x\":\"0\",\"y\":\"1\",\"page\":\"1\",\"appid\":\"f19bb05ccd760f2ccb4e7cfa914e036ab768c6ee\",\"widget\":\"f19bb05ccd760f2ccb4e7cfa914e036ab768c6ee_recommend\",\"w\":\"4\",\"h\":\"1\"},{\"x\":\"2\",\"y\":\"2\",\"page\":\"1\",\"appid\":\"f19bb05ccd760f2ccb4e7cfa914e036ab768c6ee\",\"widget\":\"f19bb05ccd760f2ccb4e7cfa914e036ab768c6ee_kaoshi\",\"w\":\"2\",\"h\":\"1\"},{\"x\":\"0\",\"y\":\"0\",\"page\":\"1\",\"appid\":\"f19bb05ccd760f2ccb4e7cfa914e036ab768c6ee\",\"widget\":\"f19bb05ccd760f2ccb4e7cfa914e036ab768c6ee_weather\",\"w\":\"4\",\"h\":\"1\"},{\"x\":\"0\",\"y\":\"3\",\"page\":\"1\",\"appid\":\"f19bb05ccd760f2ccb4e7cfa914e036ab768c6ee\",\"widget\":\"f19bb05ccd760f2ccb4e7cfa914e036ab768c6ee_classtable\",\"w\":\"4\",\"h\":\"1\"},{\"x\":\"0\",\"y\":\"2\",\"page\":\"1\",\"appi