INSERT INTO `app_code` VALUES (47, 3, '<style type=\"text/css\">\n	.body{\n		width: 100%;\n		height: 100%;\n		overflow:hidden;\n		position: relative;\n	}\n	.wallpaper{\n		position: absolute;\n		width:100%;\n		height: 100%;\n		left:0;\n		top:0;\n		z-index: -1;\n		overflow: hidden;\n		background-size: 100% 100%;\n		background-position: center center;/*background-image: url(\'\');*/\n	}\n	.headimg{\n		position: absolute;\n		top:5%;\n		left:5%;\n		width:40px;\n		height: 40px;\n		border-radius: 50%;\n	}\n	#desktop{\n		width:200vw;\n		height:100%;\n	}\n	.desktop_page{\n		width:100vw;\n		height:100%;\n		float:left;\n		position: relative;\n	}\n	.app {\n			text-align: center;\n			display: flex;\n			justify-content: center;\n			flex-direction: column;\n			position: absolute;\n			padding: 4px;\n			box-sizing: border-box;\n			transition: transform 0.2s,opacity 0.2s;\n	}\n	.app>view {\n			width: 36px;\n			height: 36px;\n			overflow: hidden;\n			border-radius: 4px;\n			margin: 0 auto;\n			margin-bottom: 12px;\n			background-size: 100% 100%;\n	}\n	.app>text {\n			color: #dddddd;\n			font-size: 11.5px;\n			overflow: hidden;\n			text-overflow: ellipsis;\n			white-space: nowrap;\n			text-shadow: 1px 1px 2px #000;\n	}\n	.app:hover {\n			border: 1px solid rgba(255,255,255,0.25);\n			background-color: rgba(239,255,236,0.25);\n	}\n	.widget {\n			overflow: hidden;\n			position: absolute;\n			transition: all 0.2s;\n	}\n	#rubish{\n			position: absolute;\n			bottom: 0;\n			width: 100%;\n			transition: all 0.2s;\n			height: 0px;\n			overflow:hidden;\n			text-align:center;\n			z-index: 1;\n	}\n	.rubish_free::after{\n		content: \'\';\n			width: 140%;\n			height: 200px;\n			position: absolute;\n			left: -20%;\n			top: 0;\n			z-index: -1;\n			border-radius: 50% 50% 0 0;\n			background: #1496f1;\n			transition:all 0.3s;\n	}\n	.rubish_active::after{\n		content: \'\';\n			width: 140%;\n			height: 200px;\n			position: absolute;\n			left: -20%;\n			top: 0;\n			z-index: -1;\n			border-radius: 50% 50% 0