INSERT INTO `app_code` VALUES (63, 2, 'app({\n	setting:{\n		title:\"这是例子应用\",\n		color:\"#AA6600\",\n	},\n	data:{//应用数据\n		text:\"Hello World\",\n		\n	},\n	onLoad:function(event){//启动应用\n		//这里是应用启动时触发\n		api.toast(\"触发了onLoad事件！\");\n	},\n	onHide:function(){//应用隐藏时\n		api.toast(\"触发了onHide事件！\");\n	},\n	onShow:function(){//应用显示时\n		api.toast(\"触发了onShow事件！\");\n	},\n	onBack:function(){//用户触摸返回键时\n		//return false;//拦截返回键，应用内自行处理\n		api.toast(\"触发了onBack事件！\");\n	},\n	onUnload:function(){//应用销毁时\n		api.toast(\"触发了onUnload事件！\");\n	},\n	onEvent:function(event){//当接收到事件时触发\n		console.log(event);\n		\n		if(event.type==\"scan\"){\n			api.toast(\"扫描结果：\"+event.data);\n		}else if(event.type==\"location\"){\n			api.toast(\"位置信息：\"+JSON.stringify(event.data));\n		}else if(event.type==\"uuid\"){\n			api.toast(\"设备唯一码：\"+JSON.stringify(event.data));\n		}else if(event.type==\"device\"){\n			api.toast(\"设备信息：\"+JSON.stringify(event.data));\n		}else if(event.type==\"onSignature\"){\n			api.toast(\"数字签名\"+JSON.stringify(event.data));\n		}else{\n			api.toast(\"onEvent:\"+JSON.stringify(event));\n		}\n	},\n	onMessage:function(event1,event2){//当应用接收到其他应用发来的消息时\n		api.toast(\"触发了onMessage事件！\");\n		console.log(event1,event2);\n	},\n	onUpdate:function(event){//应用更新时\n		//拦截内置更新机制，自行处理，谨慎使用，小心应用无法正常更新。\n		api.toast(\"触发了onUpdate事件！\");\n		//return false;\n	},\n	//其它函数为自定义函数\n	buttonClick:function(event){\n		console.log(event);//点击事件的传参\n		switch(event.target.dataset.buttontype|0){\n			case 1:\n				api.reload();\n				return;\n			case 2://测试toast\n				api.toast(\"toast 测试5秒后消失\",5000);\n				return;\n			case 3://显示等待\n				api.showLoading(\"正在等待...\");\n				return;\n			case 4://隐藏等待\n				api.hideLoading();\n				return;\n			case 5://获取公钥\n				api.toast(api.get_App());\n				return;\n			case 6://获取进程句柄\n				api.toast(api.get_Pid());\n				return;\n			case 7://打开自己\n				api.app.openapp(api.get_App(),{newer:true});\n				return;\n			case 8://关闭自己\n				api.close();\n				return;\n			case 9:\n				api.update(function(){\n					api.reload();//更新完成后重启自己\n				});\n				//或者updateReload()\n				return;\n			\n			case 10:\n				api.iniSet(function(){\n					api.toast(\"已设置test为Hello World\");\n				},\"test\",\"Hello World\");\n				return;\n			case 11:\n				api.toast(api.get_deviceId());\n				return;\n			case 12:\n				api.iniGet(function(data){\n					api.toast(\"test的值为\"+data);\n				},\"test\",\"默认值\");\n				return;\n			case 13:\n				api.storage.set(\"ch1\",\"hello1\",function(){\n					api.toast(\"ch1设置完成！\");\n				});\n				api.storage.set(\"ch2\",\"hello2\",function(){\n					api.toast(\"ch2设置完成！\");\n				});\n				return;\n			case 14:\n				api.storage.get(\"ch1\",function(data){\n					api.toast(\"ch1的值为：\"+data);\n				});\n				return;\n			case 15:\n				api.storage.keys(\"ch\",function(value,key){\n					api.toast(key+\"的值为：\"+value);\n				});//获取以ch开头的键\n				return;\n			case 16:\n				api.getApiVersion(function(data){\n					api.toast(\"当前API库版本为：\"+data);\n				});\n				return;\n			case 17:\n				api.getVersion(function(data){\n					api.toast(\"当前应用版本为：\"+data);\n				});\n				return;\n			case 18:\n				//api.trigger(\"Event1\",\"Some Text\");//各应用有序接收，可以被拦截或修改\n				api.triggerAll(\"Event1\",\"Some Text\");//无序接收\n				return;\n			case 19:\n				api.sendMessage(api.get_Pid(),\"Some Value\");//给某应用传递值\n				return;\n			case 20:\n				api.vibrate(2000);\n				return;\n			case 21:\n				api.landscape();\n				return;\n			case 22:\n				api.portrait();\n				return;\n			case 23:\n				api.reboot();\n				return;\n			case 24:\n				api.copy(\"复制了一个文本！\");return;\n			case 25:\n				api.toast(api.is_Android());return;\n			case 26:\n				api.toast(api.is_IOS());return;\n			case 27:\n				api.toast(api.is_PC());return;\n			case 28:\n				api.toast(api.is_Yiban());return;\n			case 29://获取平台类型\n				/*\n					0 浏览器\n					1 安卓易班\n					2 IOS易班\n					3 微信内置浏览器\n					4 微信小程序\n					5 QQ\n					6 桂林理工大学\n				*/\n				api.toast(api.get_Platform());return;\n			case 30://获取设备类型\n				/*\n					1 PC\n					2 安卓\n					3 IOS\n				*/\n				api.toast(api.get_Device());return;\n			case 31://获取设备ua\n				api.toast(navigator.userAgent);return;\n			case 32:\n				document.getElementById(\"runResult\").value=eval(document.getElementById(\"runCode\").value);\n				return;\n			case 33:\n				document.getElementById(\"runCode\").value=\"\";\n				return;\n			case 34:\n				api.copy(document.getElementById(\"runCode\").value);\n				api.toast(\"复制成功！\");\n				return;\n			case 35:\n				api.toast(\"粘贴失败！\");\n				return;\n			case 36:\n				api.alert(\"您确定要进行此操作吗\",\"我是标题\",[\"确认\",\"取消\"],function(event){\n					console.log(event);\n					if(event.id==0){\n						api.alert(\"你点了第一个按钮\",\"\",[\"确定\"]);\n					}else if(event.id==1){\n						api.alert(\"你点了第二个按钮\",\"标题\",[\"是啊\"]);\n					}\n				});\n				/*\n				下面这种方式更高级！\n				api.alert(\n				{\n					title:\"我是标题\",\n					text:\"您确定要进行此操作吗\",\n					button:[\"确认\",\"取消\"],\n					content:[],\n					click:function(event){\n						console.log(event);\n						if(event.id==0){\n							api.alert(\"你点了第一个按钮\",\"\",[\"确定\"]);\n						}else if(event.id==1){\n							api.alert(\"你点了第二个按钮\",\"标题\",[\"是啊\"]);\n						}\n					},\n				);\n				*/\n				return;\n			case 37:\n				api.__systemUpdate();\n				api.reboot();\n				api.toast(\"成功\");\n				return;\n			case 38:\n				api.color(\"#66AA00\");\n				api.toast(\"标题栏颜色修改成功1！\");\n				return;\n			case 39:\n				api.color(\"#6600DD\");\n				api.toast(\"标题栏颜色修改成功2！\");\n				return;\n			case 40:\n				api.title(\"标题1\");\n				api.toast(\"标题栏内容修改成功1！\");\n				return;\n			case 41:\n				api.title(\"标题2\");\n				api.toast(\"标题栏颜色修改成功2！\");\n				return;\n			case 42:\n				api.scan(function(res){\n					console.log(\"scan result\",res);\n				});\n				return;\n			case 43:\n				api.phone(\"13888888888\",function(res){\n					console.log(res);\n				});\n				return;\n			case 44:\n				api.location(function(res){\n					console.log(\"result\",res);\n				});\n				return;\n			case 45:\n				api.uuid(function(res){\n					console.log(\"result\",res);\n				});\n				return;\n			case 46:\n				api.deviceInfo(function(res){\n					console.log(\"result\",res);\n				});\n				return;\n			case 47:\n				api.AppSignature(function(res){\n					console.log(\"result\",res);\n				});\n				return;\n			case 48:\n				api.socket({type:\"message_push\",data:{title:666,content:{\n					tag:\"view\",\n					attribute:{\"style\":\"background-color:#666666;border-radius:50%;padding:10px;text-align:center;\"},\n					text:\"测试成功！这里是自定义内容！\",\n				}}},function(e){\n					console.log(e)\n				})\n				return;\n			case 49:\n				api.toast(\"成功：\"+\n					setTimeout(function(){\n						alert(\"ok\");\n					},1000)\n				);\n				return;\n			case 50:\n				api.__systemClear();\n				api.toast(\"请稍等...\");\n				return;\n			default:\n				api.toast(\"无效的按钮！\");\n		}\n		\n	},\n	\n});', '2020-01-08 13:04:25', NULL, 6, 2);
INSERT INTO `app_code` VALUES (64, 2, '<style>\nhtml,body{\n    height:100%;\n}\n.btn{\n	color: #FFFFFF; \n	background-color:rgb(1, 165, 237);\n	width:90%;\n	margin:10% auto;\n	text-align: center;\n	padding: 15px 20px;\n	border-radius: 10px;\n}\n</style>\n<view style=\"position:fixed;left:0;top:0;width:100%;height:100%;\">\n<iscroll-y>\n<view style=\"height:auto;\">\n<view class=\"alert alert-primary\">简单测试：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"2\" bindtap=\"buttonClick\">测试toast</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"3\" bindtap=\"buttonClick\">显示加载框</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"4\" bindtap=\"buttonClick\">隐藏加载框</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"36\" bindtap=\"buttonClick\">弹出对话框</button>\n\n<view class=\"alert alert-primary\">获取类：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"5\" bindtap=\"buttonClick\">获取应用的AppId</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"6\" bindtap=\"buttonClick\">获取应用的Pid（进程句柄）</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"11\" bindtap=\"buttonClick\">获取设备标识</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"16\" bindtap=\"buttonClick\">获取API库版本号</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"17\" bindtap=\"buttonClick\">获取当前应用版本号</button>\n<view class=\"alert alert-primary\">操作类：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"1\" bindtap=\"buttonClick\">重启自己</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"7\" bindtap=\"buttonClick\">打开自己</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"8\" bindtap=\"buttonClick\">关闭自己</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"9\" bindtap=\"buttonClick\">更新自己</button>\n\n\n<view class=\"alert alert-primary\">存储类：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"10\" bindtap=\"buttonClick\">设置test配置</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"12\" bindtap=\"buttonClick\">读取test配置</button>\n\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"13\" bindtap=\"buttonClick\">设置数据</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"14\" bindtap=\"buttonClick\">读取数据</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"15\" bindtap=\"buttonClick\">遍历数据</button>\n<view class=\"alert alert-primary\">消息类：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"18\" bindtap=\"buttonClick\">发送广播（unSafe）</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"19\" bindtap=\"buttonClick\">向某应用传递消息（Safe）</button>\n<view class=\"alert alert-primary\">设备类：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"20\" bindtap=\"buttonClick\">设备震动2s</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"21\" bindtap=\"buttonClick\">设备横向显示</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"22\" bindtap=\"buttonClick\">设备纵向显示</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"23\" bindtap=\"buttonClick\">重启系统</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"37\" bindtap=\"buttonClick\">更新系统</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"24\" bindtap=\"buttonClick\">复制文本</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"38\" bindtap=\"buttonClick\">标题栏颜色1</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"39\" bindtap=\"buttonClick\">标题栏颜色2</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"40\" bindtap=\"buttonClick\">标题栏文字1</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"41\" bindtap=\"buttonClick\">标题栏文字2</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"50\" bindtap=\"buttonClick\">清空缓存</button>\n\n<view class=\"alert alert-primary\">接口：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"25\" bindtap=\"buttonClick\">设备是否Android</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"26\" bindtap=\"buttonClick\">设备是否IOS</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"27\" bindtap=\"buttonClick\">设备是否PC</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"28\" bindtap=\"buttonClick\">平台是否易班</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"29\" bindtap=\"buttonClick\">获取平台类型</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"30\" bindtap=\"buttonClick\">获取设备类型</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"31\" bindtap=\"buttonClick\">获取UA</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"42\" bindtap=\"buttonClick\">扫描二维码</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"43\" bindtap=\"buttonClick\">拨打电话</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"44\" bindtap=\"buttonClick\">获取地理位置</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"45\" bindtap=\"buttonClick\">获取UUID</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"46\" bindtap=\"buttonClick\">获取device</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"47\" bindtap=\"buttonClick\">获取App数字签名</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"48\" bindtap=\"buttonClick\">发送通知消息！</button>\n\n<view class=\"alert alert-primary\">自定义代码</view>\n<textarea id=\"runCode\" style=\"width:100%;height:100px;\"></textarea>\n<textarea id=\"runResult\" style=\"width:100%;height:50px;\"></textarea>\n\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"32\" bindtap=\"buttonClick\">运行</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"33\" bindtap=\"buttonClick\">清空</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"34\" bindtap=\"buttonClick\">复制代码</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"35\" bindtap=\"buttonClick\">粘贴代码</button>\n<button class=\"ch-ripple ripple-btn\" style=\"width:100%;height:100px;\">涟漪效果例子</button>\n<div style=\"width: 90%;background-color: #ffffff;padding: 10px;margin:0 auto;\">\n	<ch-switch style=\"display: block;float: right;margin-right: 10px;\"></ch-switch>\n	<span style=\"height: 20px;line-height: 20px;font-size: 15px;color:#444444;\">开关按钮测试</span>\n</div>\n<style type=\"text/css\">\n	@-o-keyframes ch_bar{from{background-position:40px 0}to{background-position:0 0}}\n	@keyframes ch_bar{from{background-position:40px 0}to{background-position:0 0}}\n</style>\n<body>\n	<ch-progress height=\"30px\" width=\"80%\"></ch-progress>\n	<ch-progress></ch-progress>\n</body>\n<ch-slider style=\"background-color:#ffffff;padding:20px;\"></ch-slider>\n<view class=\"alert alert-primary\">测试区</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"49\" bindtap=\"buttonClick\">测试setTimeout</button>\n</view>\n</iscroll-y>\n</view>\n\n\n\n', '2020-01-08 13:04:58', NULL, 6, 1);
