se;\n		}\n		if(new Date().getTime()-2000<this.data.backTime){\n			if(api.is_Yiban()||api.is_App()){\n				api.exit();\n				return false;\n			}\n			return;\n		}//不拦截//2s\n		this.data.backTime=new Date().getTime();\n		api.toast(\"再按一次返回键退出\");\n		setTimeout(function(){api.history_delete();},1);\n		setTimeout(function(){\n			api.history_add();\n		},2000);\n		return false;\n	},\n	onUnload: function() { //应用销毁时\n		// console.log(\"触发了onUnload事件！\");\n	},\n	onEvent: function(event) { //当接收到事件时触发\n		console.log(\"desktop\",event);\n		// console.log(\"触发了onEvent事件！\",event);\n		if(event.name==\"onChangeApp\"){//收到应用修改通知\n			app.changeApp();\n		}else if(event.name==\"onLoginChange\"){\n			app.userInit();\n		}\n	},userInit:function(){\n		api.socket({id:\"user\",type:\"user\"},function(callback){\n			// console.log(callback);\n			if(callback){//登入了\n				// document.getElementById(\"realname\").innerText=callback.realname;setAttribute(\"src\",callback.userhead);\n				document.getElementById(\"user_head\").style.backgroundImage=\'url(\'+callback.userhead+\')\';\n			}else document.getElementById(\"user_head\").style.backgroundImage=\'url(\"http://img02.fs.yiban.cn/100/avatar/user/200\")\';\n		});\n	},pushQueue:function(fn){\n		app.data.queueList.push(fn);\n		app.checkQueue();\n	},checkQueue:function(){//检查\n		if(app.data.queueStatus)return;\n		if(app.data.queueList.length){\n			app.data.queueStatus=true;//非空闲状态\n			var a=app.data.queueList.shift();//移除第1个元素\n			var finish=function(){\n				app.data.queueStatus=false;\n				app.checkQueue();\n			};\n			a&&a(finish);//开始执行\n		}else app.data.queueStatus=false;\n	},onMessage:function(message){//应用间传递消息\n		if(message.referer.app==\"2203cd2aca41984b6fce63cf801a1f4cf2e234c7\"||message.referer.app==\"system\"){//引导\n			if(message.data.type==\"push\"){\n				console.log(\"收到推送\",message.data);\n				app.receivePushMessage(message.data.source);\n			}\n		}\n		api.bind();\n	},onUpdate: function(event) { //应用更新时\n		//应用自动更新机制，处理不正确将导致应用无法正常更新。\n		// console.log(\"触发了onUpdate事件！\");\n		//return false;\n	},openapp:function(event,target){\n		api.toast(\"启动应用中...\");\n		api.app.openapp(target.getAttribute(\"data-appid\"),{event:event});\n	}\n});', '2020-01-08 13:11:08', NULL, 2, 2);
INSERT INTO `app_code` VALUES (69, 5, '<style>\nhtml,body{\n    height:100%;\n}\n.btn{\n	color: #FFFFFF; \n	background-color:rgb(1, 165, 237);\n	width:90%;\n	margin:2px auto;\n	text-align: center;\n	padding: 10px 20px;\n	border-radius: 10px;\n	border:0;\n}\n</style>\n<view style=\"position:fixed;left:0;top:0;width:100%;height:100%;\">\n<iscroll-y>\n<view style=\"height:auto;text-align: center;\">\n<view class=\"alert alert-primary\">简单测试：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"2\" bindtap=\"buttonClick\">测试toast</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"3\" bindtap=\"buttonClick\">显示加载框</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"4\" bindtap=\"buttonClick\">隐藏加载框</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"36\" bindtap=\"buttonClick\">弹出对话框</button>\n\n<view class=\"alert alert-primary\">获取类：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"5\" bindtap=\"buttonClick\">获取应用的AppId</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"6\" bindtap=\"buttonClick\">获取应用的Pid（进程句柄）</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"11\" bindtap=\"buttonClick\">获取设备标识</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"16\" bindtap=\"buttonClick\">获取API库版本号</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"17\" bindtap=\"buttonClick\">获取当前应用版本号</button>\n<view class=\"alert alert-primary\">操作类：</view>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"1\" bindtap=\"buttonClick\">重启自己</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"7\" bindtap=\"buttonClick\">打开自己</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=\"8\" bindtap=\"buttonClick\">关闭自己</button>\n<button class=\"btn btn-info btn-block ch-ripple\" data-buttonType=